import React from 'react';
import { useNavigate } from 'react-router-dom';
import { getToolColorClass } from '../../../utils/helpers';
import './ToolCard.css';

const ToolCard = ({ tool }) => {
    const navigate = useNavigate();

    const handleToolClick = () => {
        navigate(`/tool/${tool.id}`);
    };

    return (
        <div
            className="tool-card rounded-xl p-6 shadow-sm border cursor-pointer"
            onClick={handleToolClick}
        >
            <div className="flex items-start justify-between">
                <div className="flex items-start space-x-4">
                    <div className={`w-12 h-12 rounded-xl ${tool.iconColor} flex items-center justify-center text-white`}>
                        <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                            <path fillRule="evenodd" d="M3 4a1 1 0 011-1h4a1 1 0 010 2H6.414l2.293 2.293a1 1 0 01-1.414 1.414L5 6.414V8a1 1 0 01-2 0V4zm9 1a1 1 0 010-2h4a1 1 0 011 1v4a1 1 0 01-2 0V6.414l-2.293 2.293a1 1 0 11-1.414-1.414L13.586 5H12zm-9 7a1 1 0 012 0v1.586l2.293-2.293a1 1 0 111.414 1.414L6.414 15H8a1 1 0 010 2H4a1 1 0 01-1-1v-4zm13-1a1 1 0 011 1v4a1 1 0 01-1 1h-4a1 1 0 010-2h1.586l-2.293-2.293a1 1 0 111.414-1.414L15 13.586V12a1 1 0 011-1z" clipRule="evenodd"></path>
                        </svg>
                    </div>
                    <div className="flex-1">
                        <h3 className="text-lg font-semibold text-custom-primary mb-2">
                            {tool.title}
                        </h3>
                        <p className="text-custom-secondary text-sm mb-4">
                            {tool.description}
                        </p>
                    </div>
                </div>
            </div>

            <div className="flex justify-end">
        <span className={`px-6 py-2 rounded-lg text-sm font-medium transition-colors ${getToolColorClass(tool.iconColor)}`}>
          {tool.buttonText}
        </span>
            </div>
        </div>
    );
};

export default ToolCard;
