import React from 'react';
import LogoGeneratorDemo from '../../demos/LogoGenerator';
import ResumeBuilderDemo from '../../demos/ResumeBuilder';
import AnalyticsDemo from '../../demos/Analytics';
import PortfolioDemo from '../../demos/Portfolio';
import './ToolDemo.css';

const ToolDemo = ({ toolId }) => {
    const renderDemo = () => {
        switch(toolId) {
            case 'logo-generator':
                return <LogoGeneratorDemo />;
            case 'resume-builder':
                return <ResumeBuilderDemo />;
            case 'analytics-dashboard':
                return <AnalyticsDemo />;
            case 'portfolio-creator':
                return <PortfolioDemo />;
            default:
                return <GenericDemo toolId={toolId} />;
        }
    };

    return (
        <div className="tool-demo">
            {renderDemo()}
        </div>
    );
};

const GenericDemo = ({ toolId }) => {
    const toolName = toolId.split('-').map(word =>
        word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');

    return (
        <div className="text-center py-12">
            <div className="w-24 h-24 bg-blue-500 rounded-2xl flex items-center justify-center mx-auto mb-6">
                <svg className="w-12 h-12 text-white" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M3 4a1 1 0 011-1h4a1 1 0 010 2H6.414l2.293 2.293a1 1 0 01-1.414 1.414L5 6.414V8a1 1 0 01-2 0V4zm9 1a1 1 0 010-2h4a1 1 0 011 1v4a1 1 0 01-2 0V6.414l-2.293 2.293a1 1 0 11-1.414-1.414L13.586 5H12zm-9 7a1 1 0 012 0v1.586l2.293-2.293a1 1 0 111.414 1.414L6.414 15H8a1 1 0 010 2H4a1 1 0 01-1-1v-4zm13-1a1 1 0 011 1v4a1 1 0 01-1 1h-4a1 1 0 010-2h1.586l-2.293-2.293a1 1 0 111.414-1.414L15 13.586V12a1 1 0 011-1z" clipRule="evenodd"></path>
                </svg>
            </div>
            <h3 className="text-xl font-semibold mb-4 text-custom-primary">{toolName} Demo</h3>
            <p className="text-custom-secondary mb-6">Interactive demo coming soon...</p>
            <div className="bg-custom-secondary rounded-lg p-8 mb-6">
                <p className="text-custom-secondary">Demo will be available here</p>
            </div>
            <button className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-lg transition-colors">
                Try {toolName}
            </button>
        </div>
    );
};

export default ToolDemo;
